example.code:
[C#]

License license = new License();
license.SetLicense(myStream);


[Visual Basic]

Dim license as License = new License
license.SetLicense(myStream)

 [Visual Basic] 

Dim chart As Chart = new Chart() 

chart.ChartArea.IsThreeD = True 

Dim series1 as ASeries = new ASeries() 
series1.ChartType = ChartType.Doughnut 
series1.DefaultDataPoint.IsLabelVisible = True 
series1.CustomAttribute.IsWithLabelMark = True 
series1.CustomAttribute.HorizontalLineSize = 50 
series1.CustomAttribute.PieLabelType = PieLabelType.Ellipse 

series1.DataPoints.Add( 30 ) 
series1.DataPoints.Add( 1 ) 
series1.DataPoints.Add( 2 ) 
series1.DataPoints.Add( 3 ) 
series1.DataPoints.Add( 4 ) 
series1.DataPoints.Add( 5 ) 
series1.DataPoints.Add( 30 ) 
series1.DataPoints.Add( 5 ) 
series1.DataPoints.Add( 4 ) 
series1.DataPoints.Add( 3) 
series1.DataPoints.Add( 2 ) 
series1.DataPoints.Add( 1 ) 

chart.NSeries.Add( series1 ) 

Me.pictureBox1.Image = chart.Save() 

[C#] 

Chart chart = new Chart(); 

chart.ChartArea.IsThreeD = true; 

ASeries series1 = new ASeries(); 
series1.ChartType = ChartType.Doughnut; 
series1.DefaultDataPoint.IsLabelVisible = true; 
series1.CustomAttribute.IsWithLabelMark = true; 
series1.CustomAttribute.HorizontalLineSize = 50; 
series1.CustomAttribute.PieLabelType = PieLabelType.Ellipse; 

series1.DataPoints.Add( 30 ); 
series1.DataPoints.Add( 1 ); 
series1.DataPoints.Add( 2 ); 
series1.DataPoints.Add( 3 ); 
series1.DataPoints.Add( 4 ); 
series1.DataPoints.Add( 5 ); 
series1.DataPoints.Add( 30 ); 
series1.DataPoints.Add( 5 ); 
series1.DataPoints.Add( 4 ); 
series1.DataPoints.Add( 3); 
series1.DataPoints.Add( 2 ); 
series1.DataPoints.Add( 1 ); 

chart.NSeries.Add( series1 ); 

this.pictureBox1.Image = chart.Save(); 

[XML] 

<Chart xmlns='Aspose.Chart'> 
		<NSeries> 
			<ASeries ChartType='Doughnut' IsLabelVisible='true' IsWithLabelMark='true' HorizontalLineSize='50' PieLabelType='Ellipse' > 
				<DataPoints> 
					<DataPoint YValues='30' /> 
					<DataPoint YValues='1' /> 
					<DataPoint YValues='2' /> 
					<DataPoint YValues='3' /> 
					<DataPoint YValues='4' /> 
					<DataPoint YValues='5' /> 
					<DataPoint YValues='30' /> 
					<DataPoint YValues='5' /> 
					<DataPoint YValues='4' /> 
					<DataPoint YValues='3' /> 
					<DataPoint YValues='2' /> 
					<DataPoint YValues='1' /> 
			</DataPoints> 
		</ASeries> 
	</NSeries> 
</Chart>

[C#] Chart chart = new Chart();
ASeries s = new ASeries(); s.ChartType = ChartType.Pie; s.DataPoints.Add( 1 ); s.DataPoints.Add( 2 ); s.DataPoints.Add( 3 ); chart.NSeries.Add( s ); chart.ChartArea.Position = new RectangleF( 50, 50, 200, 200 );
this.pictureBox1.Image = chart.Save();
[Visual Basic] Dim chart As Chart = New Chart()
Dim s As ASeries = New ASeries() s.ChartType = ChartType.Pie s.DataPoints.Add(1) s.DataPoints.Add(2) s.DataPoints.Add(3) chart.NSeries.Add(s) chart.ChartArea.Position = New RectangleF(50, 50, 200, 200)
Me.PictureBox1.Image = chart.Save()
[XML] <Chart xmlns='Aspose.Chart' >
<NSeries>
<ASeries ChartType='Pie' > <DataPoints> <DataPoint YValues='1' /> <DataPoint YValues='2' /> <DataPoint YValues='3' /> </DataPoints> </ASeries> </NSeries>
<ChartAreas> <ChartArea Left='50' Top='50' Width='200' Height='200' /> </ChartAreas> </Chart>

 [C#]
 Chart chart = new Chart( );

 ASeries s = new ASeries();
 s.ChartType = ChartType.Bubble;
 s.Name = "item1";
 s.DataPoints.Add( 1, 56, 85 );
 s.DataPoints.Add( 2, 79, 36 );
 s.DataPoints.Add( 3, 82, 55 );
 s.DataPoints.Add( 4, 67, 60 );
 s.DataPoints.Add( 5, 90, 22 );
 s.DataPoints.Add( 6, 12, 40 );
 s.DataPoints.Add( 7, 79, 90 );
 s.DataPoints.Add( 8, 80, 60 );
 s.DataPoints.Add( 9, 30, 10 ); 

 chart.NSeries.Add( s ); 

 this.pictureBox1.Image = chart.Save();


 [Visual Basic]
 Dim chart As Chart = New Chart()

 Dim s As ASeries = New ASeries()
 s.ChartType = ChartType.Bubble
 s.Name = "item1"
 s.DataPoints.Add(1, 56, 85)
 s.DataPoints.Add(2, 79, 36)
 s.DataPoints.Add(3, 82, 55)
 s.DataPoints.Add(4, 67, 60)
 s.DataPoints.Add(5, 90, 22)
 s.DataPoints.Add(6, 12, 40)
 s.DataPoints.Add(7, 79, 90)
 s.DataPoints.Add(8, 80, 60)
 s.DataPoints.Add(9, 30, 10)

 chart.NSeries.Add(s)

 Me.PictureBox1.Image = chart.Save()


 [XML]
 <Chart xmlns='Aspose.Chart' >
 	<NSeries>
			<ASeries ChartType='Bubble' Name='item1' >
 			<DataPoints>
					<DataPoint XValue='1' YValues='56;78' />
					<DataPoint XValue='2' YValues='79;36' />
					<DataPoint XValue='3' YValues='82;55' />
					<DataPoint XValue='4' YValues='67;60' />
					<DataPoint XValue='5' YValues='90;22' />
					<DataPoint XValue='6' YValues='12;40' />
					<DataPoint XValue='7' YValues='79;90' />
					<DataPoint XValue='8' YValues='80;60' />
					<DataPoint XValue='9' YValues='30;10' />
				</DataPoints>
			</ASeries>
		<NSeries>
	</Chart>

