[C#]
Chart chart = new Chart();

ASeries s = new ASeries();
s.ChartType = ChartType.Bar;
s.DataPoints.Add( 1 ); 
s.DataPoints.Add( 2 );
s.DataPoints.Add( 3 );
chart.NSeries.Add( s ); 

Axis axisY = chart.ChartArea.AxisY;
AxisLabel label = new AxisLabel( axisY ); 

label.Value = 3;
label.Format = "C";
label.FontAngle = 30;
label.Font = new Font("Times New Roman", 10);
label.Color = Color.Red;

axisY.AxisLabels.Add( label );

this.pictureBox1.Image = chart.Save();


