Example 1:
  [C#]
  Chart chart = new Chart();
  ASeries s = new ASeries();
  s.ChartType = ChartType.Bar;
  s.DataPoints.Add( new DataPoint( "Jan.", 1 ) ); 
  s.DataPoints.Add( new DataPoint( "Feb.", 2) );
  s.DataPoints.Add( new DataPoint( "Mar.", 3 ) ); 	
  s.DataPoints.Add( new DataPoint( "Apr.", 4 ) );		
  s.DataPoints.Add( new DataPoint( "May.", 5 ) );
  chart.NSeries.Add( s );
  chart.ChartArea.AxisX.AxisLabels.IsDataPointNameVisible = true;
  this.pictureBox1.Image = chart.Save();


  [Visual Basic]
  Dim chart As Chart = New Chart()
  Dim s As ASeries = New ASeries()
  s.ChartType = ChartType.Bar
  s.DataPoints.Add(new DataPoint("Jan.", 1))
  s.DataPoints.Add(new DataPoint("Feb.", 2))
  s.DataPoints.Add(new DataPoint("Mar.", 3))	
  s.DataPoints.Add(new DataPoint("Apr.", 4))	
  s.DataPoints.Add(new DataPoint("May.", 5))
  chart.NSeries.Add(s)
  chart.ChartArea.AxisX.AxisLabels.IsDataPointNameVisible = True
  Me.PictureBox1.Image = chart.Save()


  [XML]
  <Chart xmlns='Aspose.Chart' >
  	<NSeries>
  		<ASeries ChartType='Bar' >
  			<DataPoints>
  				<DataPoint YValues='1' Name='Jan.' />
  				<DataPoint YValues='2' Name='Feb.' />
  				<DataPoint YValues='3' Name='Mar.' />
  				<DataPoint YValues='4' Name='Apr.' />
  				<DataPoint YValues='5' Name='May.' />
  			</DataPoints>
  		</ASeries>
  	</NSeries>
  	<ChartAreas>
  		<ChartArea>
  			<AxisX  IsDataPointNameVisible='true' />
  		</ChartArea>
  	</ChartAreas>
  </Chart>



  Example 2:
  [C#]
  Chart chart = new Chart();

  ASeries s = new ASeries();
  s.ChartType = ChartType.Bar;
  s.DataPoints.Add( 1 );
  s.DataPoints.Add( 2 );
  s.DataPoints.Add( 3 );
  s.DataPoints.Add( 4 );
  s.DataPoints.Add( 5 );
  chart.NSeries.Add( s );

  Axis axisY = chart.ChartArea.AxisY;
  axisY.DefaultLabel.Color = Color.Blue;
  axisY.DefaultLabel.FontAngle = 20;
  axisY.DefaultLabel.Format = "C";

  AxisLabel myLabel = new AxisLabel( axisY );
  myLabel.Color = Color.Red;
  myLabel.Value = 0;
  myLabel.FontAngle = 20;
  myLabel.Format = "C";
  axisY.AxisLabels.Add( myLabel );

  axisY.AxisLabels.RemoveAt( 0 ) ;

  this.pictureBox1.Image = chart.Save();


  [Visual Basic]
  Dim chart As Chart = New Chart()

  Dim s As ASeries = New ASeries()
  s.ChartType = ChartType.Bar
  s.DataPoints.Add(1)
  s.DataPoints.Add(2)
  s.DataPoints.Add(3)
  s.DataPoints.Add(4)
  s.DataPoints.Add(5)
  chart.NSeries.Add(s)

  Dim axisY As Axis = chart.ChartArea.AxisY
  axisY.DefaultLabel.Color = Color.Blue
  axisY.DefaultLabel.FontAngle = 20
  axisY.DefaultLabel.Format = "C"

  Dim myLabel As AxisLabel = New AxisLabel(axisY)

  myLabel.Color = Color.Red
  myLabel.Value = 0
  myLabel.FontAngle = 20
  myLabel.Format = "C"

  axisY.AxisLabels.Add(myLabel)

  axisY.AxisLabels.RemoveAt(0)

  Me.PictureBox1.Image = chart.Save()

