[C#]
private void OnChartPaint(object sender, Aspose.Chart.PaintEventArgs args)
{	
	args.ChartGraphics.DrawEllipse( new Pen( Color.Red, 1 ), args.Position );
}

Chart chart = new Chart( ImageChart );
chart.Paint += new Aspose.Chart.PaintEventHandler( OnChartPaint );

ASeries s = new ASeries(); 
s.ChartType = ChartType.Bar;
s.DataPoints.Add( 1 ); 
s.DataPoints.Add( 2 ); 
s.DataPoints.Add( 3 ); 
chart.NSeries.Add( s );
this.pictureBox1.Image = chart.Save(); 


