[C#]
Chart chart = new Chart();

Title title = new Title();
title.Text = "My Chart Title";

Title title2 = new Title();
title2.Text = "Sales Report";
title2.Font = new Font( "Times New Roman",12 );
title2.Color = Color.DarkRed;
title2.FontAngle = 10;

Title titleBottom = new Title();
titleBottom.Text = "Created by Aspose Support.";
titleBottom.Alignment = StringAlignment.Far;
titleBottom.Color = Color.Brown;
titleBottom.Font = new Font( "Times New Roman",10,System.Drawing.FontStyle.Bold );
titleBottom.HorizontalSpace = 10;
titleBottom.PositionType = TitlePositionType.Bottom;

chart.Titles.Add( title );
chart.Titles.Add( title2 );
chart.Titles.Add( titleBottom );

ASeries s = new ASeries(); 
s.ChartType = ChartType.Bar;
s.DataPoints.Add( 1 ); 
s.DataPoints.Add( 2 );
s.DataPoints.Add( 3 );
s.DataPoints.Add( 4 );
chart.NSeries.Add( s );

chart.FrameBorder.BorderType = BorderType.Colonial;
chart.FrameBorder.BorderColor = Color.Gray;
this.pictureBox1.Image = chart.Save(); 


