		<Title Text='Created by Aspose Support.' Alignment='Far' Color='Brown' Font='Times New Roman,10' HorizontalSpace='10' PositionType='Bottom' />		
	</Titles>
	<NSeries>
		<ASeries ChartType='Bar' >
			<DataPoints>
				<DataPoint YValues='1' />
				<DataPoint YValues='2' />
				<DataPoint YValues='3' />
				<DataPoint YValues='4' />
			</DataPoints>
		</ASeries>
	</NSeries>
	<FrameBorder BorderType='Colonial' BorderColor='Gray' />
</Chart>

