  Example 2:
  [C#]
  Chart chart = new Chart();

  ChartArea area2 = new ChartArea( chart );
  string secondAreaName = "ChartArea2";
  area2.Name = secondAreaName;
  area2.BackColor=Color.Empty;
  chart.ChartAreas.Add( area2 );

  ASeries series1 = new ASeries();
  series1.ChartType = ChartType.Bar;
  series1.DataPoints.Add( 1 ); 
  series1.DataPoints.Add( 2 );
  series1.DataPoints.Add( 3 );
  chart.NSeries.Add( series1 ); 

  ASeries series2 = new ASeries();
  series2.ChartType = ChartType.Pie;
  series2.ChartArea = secondAreaName;
  series2.DataPoints.Add( 1 ); 
  series2.DataPoints.Add( 2 );
  series2.DataPoints.Add( 3 );
  chart.NSeries.Add( series2 );

  chart.ChartAreaLayout = LayoutType.Custom;		
  area2.Position = new RectangleF( 100, 150, 250, 250 ); 
  chart.ChartArea.Position = new RectangleF( 50, 50, 400, 250 ); 

  this.pictureBox1.Image = chart.Save();


  [Visual Basic] 
  Dim chart As Chart = New Chart()

  Dim area2 As ChartArea = New ChartArea(chart)
  Dim secondAreaName As String = "ChartArea2"
  area2.Name = secondAreaName
  area2.BackColor = Color.Empty
  chart.ChartAreas.Add(area2)

  Dim series1 As ASeries = New ASeries()
  series1.ChartType = ChartType.Bar
  series1.DataPoints.Add(1)
  series1.DataPoints.Add(2)
  series1.DataPoints.Add(3)
  chart.NSeries.Add(series1)

  Dim series2 As ASeries = New ASeries()
  series2.ChartType = ChartType.Pie
  series2.ChartArea = secondAreaName
  series2.DataPoints.Add(1)
  series2.DataPoints.Add(2)
  series2.DataPoints.Add(3)
  chart.NSeries.Add(series2)

  chart.ChartAreaLayout = LayoutType.Custom
  area2.Position = New RectangleF(100, 150, 250, 250)
  chart.ChartArea.Position = New RectangleF(50, 50, 400, 250)

  Me.PictureBox1.Image = chart.Save()


  [XML]
  <Chart xmlns='Aspose.Chart' ChartAreaLayout='Custom' >
  	<NSeries>
  		<ASeries ChartType='Bar' >
  			<DataPoints>
  				<DataPoint YValues='1' />
  				<DataPoint YValues='2' />
  				<DataPoint YValues='3' />
  			</DataPoints>
  		</ASeries>

  		<ASeries ChartType='Pie' ChartArea='ChartArea2' >
  			<DataPoints>
  				<DataPoint YValues='1' />
  				<DataPoint YValues='2' />
  				<DataPoint YValues='3' />
  			</DataPoints>
  		</ASeries>
  	</NSeries>
  	<ChartAreas>
  		<ChartArea left='50' top='50' width='400' height='250' />
  		<ChartArea name='ChartArea2' BackColor='Empty' left='100' top='150' width='250' height='250' />
  	</ChartAreas>
  </Chart>





