  Example 4:
  [C#]
  Graphics g = this.CreateGraphics();
  IntPtr hdc = g.GetHdc();

  Metafile mf = new System.Drawing.Imaging.Metafile( @"c:\test.wmf", hdc );

  /*
  MemoryStream memStream = new MemoryStream(); 
  Metafile mf = new System.Drawing.Imaging.Metafile( memStream, hdc );
  */

  g.ReleaseHdc(hdc);
  g.Dispose();

  Chart chart = new Chart();
  chart.Graphics = Graphics.FromImage( mf );

  ASeries s = new ASeries();
  s.ChartType = ChartType.Bar;
  s.DataPoints.Add( 1 );
  s.DataPoints.Add( 2 );
  s.DataPoints.Add( 3 );
  s.DataPoints.Add( 4 );
  chart.NSeries.Add( s );

  chart.Save();
  pictureBox1.Image = mf;

  chart.Graphics.Dispose();


  [Visual Basic]
  Dim g As Graphics = Me.CreateGraphics()
  Dim hdc As IntPtr = g.GetHdc()

  Dim mf As Metafile = New System.Drawing.Imaging.Metafile("c:\test.wmf", hdc)

  ' Dim memStream As MemoryStream = New MemoryStream()
  ' Dim mf As Metafile = New System.Drawing.Imaging.Metafile(memStream, hdc)

  g.ReleaseHdc(hdc)
  g.Dispose()

  Dim chart As Chart = New Chart()
  chart.Graphics = Graphics.FromImage(mf)

  Dim s As ASeries = New ASeries()
  s.ChartType = ChartType.Bar
  s.DataPoints.Add(1)
  s.DataPoints.Add(2)
  s.DataPoints.Add(3)
  s.DataPoints.Add(4)
  chart.NSeries.Add(s)

  chart.Save()
