  Example 5:
  Create Flash Chart

  [C#] 
  Chart myChart = new Chart(); 
  myChart.ChartArea.Transparence = 255; 
  myChart .FrameBorder.BorderType = BorderType.Arch; 

  ASeries series = new ASeries(); 
  series.ChartType = ChartType.Pie; 
  series.DataPoints.Add( new DataPoint( "Jan.", 1 ) ); 
  series.DataPoints.Add( new DataPoint( "Feb.", 2 )); 
  series.DataPoints.Add( new DataPoint( "Mar.", 3 ) ); 
  series.DataPoints.Add( new DataPoint( "Apr.", 4 ) ); 
  series.DataPoints.Add( new DataPoint( "May.", 5 ) ); 
  series.DataPoints.Add( new DataPoint( "Jun.", 6 ) ); 
  series.DataPoints[0].URL = "http://www.Aspose.com"; 
  series.DataPoints[1].URL = "http://www.aspose.com/Products/Aspose.Chart/Intro/Overview.html"; 
  series.DataPoints[2].URL = "http://www.aspose.com/Forums/ShowForum.aspx?ForumID=6"; 
  series.DataPoints[0].ToolTip = new Aspose.Chart.ToolTip( "http://www.Aspose.com" );
  series.DataPoints[1].ToolTip = new Aspose.Chart.ToolTip( "http://www.aspose.com/Products/Aspose.Chart/Intro/Overview.html" );
  series.DataPoints[2].ToolTip = new Aspose.Chart.ToolTip( "http://www.aspose.com/Forums/ShowForum.aspx?ForumID=6" );
  myChart.NSeries.Add( series ); 
  myChart.SaveToSwf( "myChart.swf",false ); 


  [VB.Net] 
  Dim myChart As New Chart() 
  myChart.ChartArea.Transparence = 255 
  myChart .FrameBorder.BorderType = BorderType.Arch 

  Dim series As New ASeries() 
  series.ChartType = ChartType.Pie 
  series.DataPoints.Add( new DataPoint( "Jan.", 1 ) ) 
  series.DataPoints.Add( new DataPoint( "Feb.", 2 ) ) 
  series.DataPoints.Add( new DataPoint( "Mar.", 3 ) ) 
  series.DataPoints.Add( new DataPoint( "Apr.", 4 ) ) 
  series.DataPoints.Add( new DataPoint( "May.", 5 ) ) 
  series.DataPoints.Add( new DataPoint( "Jun.", 6 ) ) 
  series.DataPoints[0].URL = "http://www.Aspose.com" 
  series.DataPoints[1].URL = "http://www.aspose.com/Products/Aspose.Chart/Intro/Overview.html"
  series.DataPoints[2].URL = "http://www.aspose.com/Forums/ShowForum.aspx?ForumID=6"
  series.DataPoints[0].ToolTip = new Aspose.Chart.ToolTip( "http://www.Aspose.com" )
  series.DataPoints[1].ToolTip = new Aspose.Chart.ToolTip( "http://www.aspose.com/Products/Aspose.Chart/Intro/Overview.html" )
  series.DataPoints[2].ToolTip = new Aspose.Chart.ToolTip( "http://www.aspose.com/Forums/ShowForum.aspx?ForumID=6" )
  myChart.NSeries.Add(series) 

  myChart.SaveToSwf("myChart.swf", False) 


  [XML] 
  <Chart xmlns='Aspose.Chart' SmoothingMode='HighQuality' > 
  	<NSeries> 
  		<ASeries ChartType='Pie' > 
  		<DataPoints> 
  			<DataPoint YValues='1' Name="Jan." URL="http://www.Aspose.Com" >
  			<ToolTip Text="http://www.Aspose.Com"/></DataPoint>
  			<DataPoint YValues='2' Name="Feb." URL="http://www.aspose.com/Products/Aspose.Chart/Intro/Overview.html" > 
  			<ToolTip Text="http://www.aspose.com/Products/Aspose.Chart/Intro/Overview.html"/></DataPoint>
  			<DataPoint YValues='3' Name="Mar." URL="http://www.aspose.com/Forums/ShowForum.aspx?ForumID=6" > 
