[C#]

string currentDir = System.IO.Directory.GetCurrentDirectory(); 
string licenseFile = currentDir + " \\Aspose.Chart.lic";
Chart.SetLicense(licenseFile); 

[Visual Basic]

Dim currentDir As String = System.IO.Directory.GetCurrentDirectory() 
Dim licenseFile As String = currentDir + "\Aspose.Chart.lic"
Chart.SetLicense(licenseFile)

[C#]

string licenseFile = MapPath("License") + "\\Aspose.Chart.lic"; 
Chart.SetLicense(licenseFile, this); 

[Visual Basic]

Dim licenseFile As String = MapPath("License") + "\Aspose.Chart.lic"
Chart.SetLicense(licenseFile, Me)

Expample1:
  [C#]
  Chart chart = new Chart( );
  string xmlFile = "C:\\MyData.Xml";

  chart.BindXML( xmlFile );

  string imageFile = chart.GetTmpFileName( path, ImageFormat.Png );
  chart.Save( imageFile, true );

  Image image = Image.FromFile( imageFile );
  this.pictureBox1.Image = image;


  [Visual Basic]
  Dim chart as Chart = new Chart()
  dim xmlFile = "C:\MyData.Xml"

  chart.BindXML(xmlFile)

  dim imageFile = chart.GetTmpFileName( path, ImageFormat.Png )
  chart.Save(imageFile, True)

  Dim image As Image = Image.FromFile(imageFile)
  Me.PictureBox1.Image = image


