[C#] Chart myChart = new Chart();
ASeries series = new ASeries(); series.ChartType = ChartType.Pie; series.DataPoints.Add( 1 ); series.DataPoints.Add( 2 ); series.DataPoints.Add( 3 ); series.DataPoints.Add( 4 ); series.DataPoints.Add( 5 ); series.DataPoints[0].URL = "http://www.Aspose.com"; myChart.NSeries.Add( series ); myChart.SaveToSwf( "ttt.swf",false );
[VB.Net] Dim myChart As New Chart()
Dim series As New ASeries() series.ChartType = ChartType.Pie series.DataPoints.Add(1) series.DataPoints.Add(2) series.DataPoints.Add(3) series.DataPoints.Add(4) series.DataPoints.Add(5) series.DataPoints[0].URL = "http://www.Aspose.com" myChart.NSeries.Add(series) myChart.SaveToSwf("ttt.swf", False)
[XML] <Chart xmlns='Aspose.Chart' SmoothingMode='HighQuality' > <NSeries> <ASeries ChartType='Pie' > <DataPoints> <DataPoint YValues='1' URL="http://www.Aspose.Com" /> <DataPoint YValues='2' /> <DataPoint YValues='3' /> <DataPoint YValues='4' /> <DataPoint YValues='5' /> </DataPoints> </ASeries> </NSeries> </Chart>

[C#]
void GetChartGraphics( Aspose.Chart chart, Graphics g )
{
	Bitmap bitmap = chart.Save();
	g = Graphics.FromImage( bitmap ); 
}


[Visual Basic]
Private Sub GetChartGraphics(ByVal chart As Aspose.Chart, ByVal g As Graphics)
	Dim bitmap As Bitmap = chart.Save()
	g = Graphics.FromImage(bitmap)
End Sub
