TextColumn c1 = columns[0];
c1.Width = 100;
c1.SpaceAfter = 20;

//Set the second column to take the rest of the space available on the page.
TextColumn c2 = columns[1];
c2.Width = builder.PageSetup.UseablePageWidth - c1.Width - c1.SpaceAfter;

