{
	//if we wanted to change any properties, they could be done here
	//...
	//...

	//if we wanted to cancel this lookup, set
	// e.Cancel = true

	Console.WriteLine( "BeforeLookup called for DNSBlackList: {0}", e.DNSBlackList.Name );
}

