Console.WriteLine( h.ValueComplete );

Header h = new Header( );
h.Name = "Subject";

//encode the header for MIME compliance
h.SetHeaderValue( "espaol", MimeEncoding.Base64, "iso-8859-1" );

//write out the formatted header
Console.WriteLine( h.ToString() );

